se {                    selectDate += $(this).html();                }                i++;            });            var selectPrice = new Array();            i = 0;            $('#selected-box div.piaojia').each(function() {                selectPrice[i] = $(this).html();                i++;            });            var Objid = new Array();            var ticketBuyCount = new Array();            var priceArray = new Array();            var selectPriceIsTourist = new Array();            i = 0;            $('#selected-box input').each(function() {                ticketBuyCount[i] = $(this).val();                priceArray[i] = $(this).attr("price");                Objid[i] = $(this).attr("objid");                selectPriceIsTourist[i] = $(this).attr("isidcard") == "isidcard" ? "True" : "False";                i++;            });            var jStr = "[";            for (var j = 0; j < selectPrice.length; j++) {                jStr += '{"ID":"' + Objid[j] + '","TicketTime":"' + selectDate + '","TicketName":"' + selectPrice[j] + '","TicketPrice":"' + priceArray[j] + '","TicketCount":"' + ticketBuyCount[j] + '","IsIdCard":"' + selectPriceIsTourist[j] + '"}';                if (j != selectPrice.length - 1) {                    jStr += ',';                } else {                    jStr += ']';                }            }            //var json = '{"PerformName":"美林湖温泉","PerformTime":"' + selectDate + '","PerformAddress":"广东省广东省广州市花都区狮岭镇山前旅游大道168号花都区狮岭镇山前旅游大道168号","TicketInfo":[' + jStr + ']}';            var json = '{"PerformName":"576O5p6X5rmW5rip5rOJ","PerformTime":"' + selectDate + '","PerformAddress":"5bm/5Lic55yB5bm/5Lic55yB5bm/5bee5biC6Iqx6YO95Yy654uu5bKt6ZWH5bGx5YmN5peF5ri45aSn6YGTMTY45Y+36Iqx6YO95Yy654uu5bKt6ZWH5bGx5YmN5peF5ri45aSn6YGTMTY45Y+3","PerformID":"2164","DeliveryMode":"1","PerformCity":"广州","isOnline":"True","isSubscribt":"False","ticketAddress":"广州市越秀区广州大道北新达城广场北座1003室","categories":"旅游景点"}';            if (jStr == "" || ticketBuyCount.length < 1)            {                alert("请选择您要购买场次和票价。");                return;            }            try {                var dateval = $("#appDate").val();                if (dateval == '请选择出游日期' && $(".show-date").css("display") == 'block')                {                    alert("请选择出游日期");                    return;                }                var d = new Date();                if (dateval < d) {                    alert("不可能穿越以前去游玩!");                    return;                }                $("#pdate").val(dateval);            } catch (e) {                alert("请选择出游日期");            }            if ($(".show-date").css("display") == 'block') {                var f = false;                $.ajax({                    url: '../Stock/ValidateTreavel?tickinfo=' + jStr + "&date=" + dateval,                    async: false,                    type: 'POST',                    dataType: 'JSON',                    success: function(data) {                        if (!data.Result) {                            //show_tips(data.Message, '返回重新选定');                            alert(data.Message);                            f = true;                        }                    }                });                if (f) {                    return;                }                if ($("#show-date").css("display") == "block") {                    try {                        var dateval = $("#appDate").val();                        var d = new Date();                        if (dateval < d) {                            alert("请输入出游日期!");                            return;                        }                        var myDate = new Date();                        var currdate = myDate.toLocaleDateString();     //获取当前日期                        var date1 = new Date(dateval);//输入时间           